import React, { useEffect, useState } from 'react';
import {
  Card, Container,
} from 'react-bootstrap'

function IngredientsDisplay() {
  const [ingredients, setIngredients] = useState([]);

  // Waiting for endpoint to be ready
  useEffect(() => {
    fetch('https://tarea-1.2023-1.tallerdeintegracion.cl/ingredients')
      .then((response) => response.json())
      .then((json) => setIngredients(json));
  }, []);


  return (
    <div>
      <Container className="title-text">
        Ingredientes
      </Container>
      <Container className="ingredients-container">
        {ingredients.item?.map((ingredient) => (
          <Card className="ingredient-card">
            <Card.Body>
              <Card.Title>{ingredient.name}</Card.Title>
              <Card.Text>
                {ingredient.price}
              </Card.Text>
            </Card.Body>
          </Card>
        ))}
      </Container>
    </div>
  );

}

export default IngredientsDisplay;
