import React, { useEffect, useState } from 'react';
import {
  Card, Container,
} from 'react-bootstrap'

function IngredientsDisplay() {
  const [ingredients, setIngredients] = useState([]);

  // Waiting for endpoint to be ready
  const apiGet = () => {
    fetch("https://tarea-1.2023-1.tallerdeintegracion.cl/trays")
      .then((response) => response.json())
      .then((json) => {
        console.log(json);
        setIngredients(json);
      });
  };

  useEffect(() => {
      apiGet();
    }, []);
  //isplay ingredients

  return (
    <div>
      <Container className="title-text">
        Ingredientes
      </Container>
      <Container className="ingredients-container">

        {ingredients.items?.map((ingredient) => (
          <Card className="ingredient-card">
            <Card.Body>
              <Card.Title>{ingredient.name}</Card.Title>
              <Card.Text>
                {ingredient.price}
              </Card.Text>
            </Card.Body>
          </Card>
        ))}
      </Container>
    </div>
  );

}

export default IngredientsDisplay;
