import React, { useEffect, useState } from 'react';
import {
  Card, Button, Container, Pagination, Col, Row
} from 'react-bootstrap'
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import ArrowDropUpIcon from '@mui/icons-material/ArrowDropUp';
import SortIcon from '@mui/icons-material/Sort';
import SearchIcon from '@mui/icons-material/Search';

function IngredientsDisplay() {
  const [ingredients, setIngredients] = useState([]);
  //paginación ayuda chatGPT
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 8;
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = ingredients.slice(indexOfFirstItem, indexOfLastItem);

  const totalPages = Math.ceil(ingredients.length / itemsPerPage);
  const pages = [];
  for (let i = 1; i <= totalPages; i++) {
    pages.push(
      <Pagination.Item key={i} active={i === currentPage} onClick={() => setCurrentPage(i)}>
        {i}
      </Pagination.Item>
    );
  }
  // Waiting for endpoint to be ready
  const apiGet = () => {
    fetch("https://tarea-1.2023-1.tallerdeintegracion.cl/ingredients")
      .then((response) => response.json())
      .then((json) => {
        console.log(json);
        setIngredients(json.items);
      });
  };

  useEffect(() => {
      apiGet();
    }, []);


    return (
      <div>
      <Container className="title-text">Ingredientes</Container>
      <Button id="products_button" onClick={() => handleNewSort('name')}>
                <SortIcon className="products_buttonIcon" />
                {' '}
                Ordenar Por Nombre
                {/* Manejo de cambio icono flecha orden columna */}
                {(() => {
                  if (sortBy === 'name') {
                    if (order === 1) {
                      return <ArrowDropDownIcon />;
                    }
                    return <ArrowDropUpIcon />;
                  }
                  return <> </>;
                })()}
              </Button>
      <Row xs={1} md={2} lg={4} className="g-4">
        {currentItems.map((ingredient) => (
          <Col key={ingredient.id}>
            <Card style={{ width: '200px', height: '300px' }}>
              <Card.Img variant="top" style={{ width: '200px', height: '200px' }} src={ingredient.img_url} />
              <Card.Body>
                <Card.Title>{ingredient.name}</Card.Title>
                <Card.Text>{ingredient.price}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <Pagination>{pages}</Pagination>
    </div>
    );


      // <div>
      //   <Container className="title-text">Ingredientes</Container>
      //   <CardGroup>
      //   {ingredients.map((ingredient) => (
      //         <Card>
      //         <Card.Img stylevariant="top" src={ingredient.img_url} />
      //         <Card.Body>
      //           <Card.Title>{ingredient.name}</Card.Title>
      //           <Card.Text>
      //           {ingredient.price}
      //           </Card.Text>
      //         </Card.Body>

      //       </Card>
      //       ))}

      //   </CardGroup>
      // </div>




}

export default IngredientsDisplay;
