import React, { useEffect, useState } from 'react';
import {
  Card, Button, Container,
} from 'react-bootstrap'

function IngredientsDisplay() {
  const [ingredients, setIngredients] = useState([]);

  // Waiting for endpoint to be ready
  const apiGet = () => {
    fetch("https://tarea-1.2023-1.tallerdeintegracion.cl/ingredients")
      .then((response) => response.json())
      .then((json) => {
        console.log(json);
        setIngredients(json.items);
      });
  };

  useEffect(() => {
      apiGet();
    }, []);


    return (
      <div>
        <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="holder.js/100px180" />
      <Card.Body>
        <Card.Title>Card Title</Card.Title>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
        <Container className="title-text">Ingredientes</Container>
        <CardGroup>
        <Card style={{ width: '18rem' }}>
        {ingredients.map((ingredient) => (
              <div key={ingredient.id} className="col mb-4">
                <div className="card h-100">
                  <img
                    src={ingredient.img_url}
                    className="card-img-top"
                    alt={ingredient.name}
                    style={{ height: "200px", objectFit: "cover" , objectPosition: "center", width: "200px"}}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{ingredient.name}</h5>
                    <p className="card-text">{ingredient.price}</p>
                  </div>
                </div>
              </div>
            ))}
        </Card>

        </CardGroup>
        <div className="container text-center">
        <div className="row row-cols-1 row-cols-md-2 row-cols-lg-4 g-3">
            {ingredients.map((ingredient) => (
              <div key={ingredient.id} className="col mb-4">
                <div className="card h-100">
                  <img
                    src={ingredient.img_url}
                    className="card-img-top"
                    alt={ingredient.name}
                    style={{ height: "200px", objectFit: "cover" , objectPosition: "center", width: "200px"}}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{ingredient.name}</h5>
                    <p className="card-text">{ingredient.price}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          </div>
      </div>
    );


}

export default IngredientsDisplay;
