import React, { useEffect, useState } from 'react';
import {
  Card, Button, Container, Pagination, Col, Row, Dropdown
} from 'react-bootstrap'
import { useParams } from 'react-router-dom'; 

function SingleMenu() {
  const [ingredients, setIngredients] = useState([]);
  const { id } = useParams();
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 16;
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = ingredients.courses.slice(indexOfFirstItem, indexOfLastItem);
  const [sortBy, setSortBy] = useState('name'); // column to use to sort products
  const [order, setOrder] = useState(1);

  const totalPages = Math.ceil( ingredients.courses.length / itemsPerPage);
  const pages = [];
  for (let i = 1; i <= totalPages; i++) {
    pages.push(
      <Pagination.Item key={i} active={i === currentPage} onClick={() => setCurrentPage(i)}>
        {i}
      </Pagination.Item>
    );
  }

  // Waiting for endpoint to be ready
  const apiGet = () => {
      fetch(`https://tarea-1.2023-1.tallerdeintegracion.cl/trays/${id}`)
      .then((response) => response.json())
      .then((json) => {
        console.log(json);
        setIngredients(json);
      });
  };

  useEffect(() => {
      apiGet();
    }, []);

    const handleSortByAttr = async () => {
        const sortedList = ingredients.courses.slice(0);
        sortedList.sort((a, b) => (a[sortBy] > b[sortBy] ? order : -order));
        setIngredients(sortedList);
      };

      const handleNewSort = async (column) => {
        setSortBy(column);
        setOrder(-order);
        handleSortByAttr();
      };

    return (
      <div>
      <Container className="title-text">Información de {ingredients.name}</Container>
      <Row xs={1} md={2} lg={4} className="g-4">
          <Col key={ingredients.id}>
            <Card>
              <Card.Img variant="top"  src={ingredients.img_url} />
              <Card.Body>
                <Card.Title>Nombre: {ingredients.name}</Card.Title>
                <Card.Title>Precio: {ingredients.price}</Card.Title>
                <Card.Title>Descripción:</Card.Title>
                <Card.Text>{ingredients.description}</Card.Text>
                <Card.Text>Tamaño: {ingredients.size}</Card.Text>
                <Card.Text>Expiración: {ingredients.expiration}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
      </Row>

      <Container className="title-text">Menú</Container>
      <Dropdown>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Ordenar
      </Dropdown.Toggle>

      <Dropdown.Menu>
      <Dropdown.Item> <Button onClick={() => handleNewSort('name')}>Ordenar Por Nombre</Button></Dropdown.Item>
      <Dropdown.Item> <Button onClick={() => handleNewSort('price')}>Ordenar Por Precio</Button></Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>

      <Row xs={1} md={2} lg={4} className="g-4">
        {currentItems.map((ingredient) => (
          <Col key={ingredient.id}>
            <Card >
              <Card.Body>
                <Card.Title>{ingredient.name}</Card.Title>
                <Card.Text>{ingredient.price}</Card.Text>
                <Button href={`/menu/${ingredient.id}`} variant="primary">Detalle</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <Pagination>{pages}</Pagination>
    </div>
    );
 }
export default SingleMenu;
