import React, { useEffect, useState } from 'react';
import {
  Card, Button, Container,CardGroup, Col, Row
} from 'react-bootstrap'

function IngredientsDisplay() {
  const [ingredients, setIngredients] = useState([]);

  // Waiting for endpoint to be ready
  const apiGet = () => {
    fetch("https://tarea-1.2023-1.tallerdeintegracion.cl/ingredients")
      .then((response) => response.json())
      .then((json) => {
        console.log(json);
        setIngredients(json.items);
      });
  };

  useEffect(() => {
      apiGet();
    }, []);


    return (
      <div>
        <label htmlFor="name" className="pe-1">Nombre Empresa: </label>
      <Row xs={1} md={2} className="g-4">
      {Array.from({ length: 4 }).map((_, idx) => (
        <Col>
          {ingredients.map((ingredient) => (
              <Card>
              <Card.Img stylevariant="top" src={ingredient.img_url} />
              <Card.Body>
                <Card.Title>{ingredient.name}</Card.Title>
                <Card.Text>
                {ingredient.price}
                </Card.Text>
              </Card.Body>

            </Card>
            ))}
        </Col>
      ))}
    </Row>
    </div>


      // <div>
