import React, { useEffect, useState } from 'react';
import {
  Card, Container,
} from 'react-bootstrap'

function IngredientsDisplay() {
  const [ingredients, setIngredients] = useState([]);

  // Waiting for endpoint to be ready
  const apiGet = () => {
    fetch("https://tarea-1.2023-1.tallerdeintegracion.cl/ingredients")
      .then((response) => response.json())
      .then((json) => {
        console.log(json);
        setIngredients(json.items);
      });
  };

  useEffect(() => {
      apiGet();
    }, []);


  return (
    <div>
      <Container className="title-text">
        Ingredientes
      </Container>
      <Container className="ingredients-container">

        {ingredients.map((ingredient) => (
          <div style={{ width: '18rem' }} className="card">
            <img src={ingredient.img_url} class="img-thumbnail" alt="4"></img>
            <div class="card-body"></div>
            <h5 class="card-title">{ingredient.name}</h5>
            <p class="card-text">{ingredient.price}</p>
            </div>

        ))}
      </Container>
    </div>
  );

}

export default IngredientsDisplay;
